import React, { useEffect, useState, useRef } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart } from 'chart.js/auto'; // Import the Chart.js library

function App() {
  const [attractions, setAttractions] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    fetchAttractionData();
  }, []);

  async function fetchAttractionData() {
    try {
      const [regions, cities, districts, attractions] = await Promise.all([
        fetch('http://localhost:3001/region/').then((res) => res.json()),
        fetch('http://localhost:3001/city/').then((res) => res.json()),
        fetch('http://localhost:3001/district/').then((res) => res.json()),
        fetch('http://localhost:3001/attraction/list').then((res) => res.json())
      ]);

      console.log(regions);
      console.log(cities);
      console.log(districts);
      console.log(attractions);

      setAttractions(attractions);
    } catch (error) {
      console.error('Error fetching attraction data:', error);
    }
  }

  function createGraph(attractions) {
    const regionCount = attractions.reduce((count, attraction) => {
      const region = attraction.district.city.region.name;
      count[region] = (count[region] || 0) + 1;
      return count;
    }, {});

    const labels = Object.keys(regionCount);
    const counts = Object.values(regionCount);

    const chartData = {
      labels,
      datasets: [
        {
          label: 'Attractions by Region',
          data: counts,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    const chartOptions = {
      scales: {
        x: {
          type: 'category',
          beginAtZero: true,
          maxBarThickness: 50,
          grid: {
            display: false,
          },
        },
        y: {
          beginAtZero: true,
          grid: {
            color: 'rgba(0, 0, 0, 0.1)',
            drawBorder: false,
            zeroLineColor: 'rgba(0, 0, 0, 0.1)',
          },
        },
      },
    };

    destroyChart();

    const newChart = new Chart(chartRef.current, {
      type: 'bar',
      data: chartData,
      options: chartOptions,
    });

    return newChart;
  }

  function destroyChart() {
    if (chartRef.current) {
      chartRef.current.destroy();
    }
  }

  return (
    <div>
      <h1>My App</h1>
      <div>
        <h2>Attractions by Region</h2>
        <canvas ref={chartRef} />
      </div>
      {createGraph(attractions)}
    </div>
  );
}

export default App;
