import { useState, useEffect } from 'react';
import Photo from './Photo';

function Photos() {
    const [photos, setPhotos] = useState([]);
    const history = useHistory(); // Add useHistory hook
  
    useEffect(function() {
      const getPhotos = async function() {
        const res = await fetch("http://localhost:3001/photos");
        const data = await res.json();
        setPhotos(data);
      }
      getPhotos();
    }, []);
  
    const handleClick = (photoId) => {
      history.push(`/photo/${photoId}`); // Redirect to the photo details page
    };
  
    return (
      <div>
        <h3>Photos:</h3>
        <ul>
          {photos.map(photo => (
            <Photo
              photo={photo}
              key={photo._id}
              handleClick={() => handleClick(photo._id)} // Pass the click handler to the Photo component
            />
          ))}
        </ul>
      </div>
    );
  }
  

export default Photos;
