import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Attractions() {
  const [attractions, setAttractions] = useState([]);

  useEffect(() => {
    const getAttractions = async () => {
      try {
        const res = await fetch('http://localhost:3001/attraction');
        const data = await res.json();
        const districtName = data[0].district.name;
        console.log(districtName)
        const res1 = await fetch('http://localhost:3001/district/'+districtName);
        const data1 = await res1.json();
        const cityName = data1[0];
        console.log("city:" + cityName)
        //const res2 = await fetch('http://localhost:3001/district/'+districtName);
        //const data2 = await res1.json();
        
        const combinedData = data.map((attraction) => {
          const district = data1.find((district) => district._id === attraction.districtId);
          return { ...attraction, district };
        });
        setAttractions(combinedData);
        console.log(combinedData);
      } catch (error) {
        console.error('Error fetching attractions:', error);
      }
    };
    

    getAttractions();
  }, []);

  return (
    <div>
      <h3>Attractions:</h3>
      {attractions.map((attraction, index) => (
        <div key={index}>
          <p>
            {attraction.name}, {attraction.district.name}: {attraction.coordinates[0]}, {attraction.coordinates[1]}
          </p>
        </div>
      ))}
    </div>
  );
}

export default Attractions;
