import { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Photo from './Photo';

function Photos() {
  const [photos, setPhotos] = useState([]);
  const navigate = useNavigate();
  const intersectionObserverRef = useRef(null);

  useEffect(() => {
    const getPhotos = async () => {
      try {
        document.body.style = 'background: red;'
        const res = await fetch('http://localhost:3001/photos');
        const data = await res.json();
        setPhotos(data);
      } catch (error) {
        console.error('Error fetching photos:', error);
      }
    };

    getPhotos();
  }, []);

  const handleIntersection = (photoId) => {
    console.log('View +1'); // Log when photo becomes visible in viewport
    // You can implement the logic to increment the view count here
  };

  useEffect(() => {
    intersectionObserverRef.current = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const photoId = entry.target.dataset.photoId;
          handleIntersection(photoId);
          intersectionObserverRef.current.unobserve(entry.target);
        }
      });
    });

    return () => {
      if (intersectionObserverRef.current) {
        intersectionObserverRef.current.disconnect();
      }
    };
  }, [handleIntersection]);

  return (
    <div>
      <h3>Photos:</h3>
      <ul>
        {photos.map((photo) => (
          <Photo
            photo={photo}
            key={photo._id}
            handleClick={() => navigate(`/photo/${photo._id}`)}
            intersectionObserverRef={intersectionObserverRef}
          />
        ))}
      </ul>
    </div>
  );
}

export default Photos;
