import React, { useState, useEffect } from 'react';

function CreateAttraction() {
  const [regions, setRegions] = useState([]);
  const [cities, setCities] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [attraction, setAttraction] = useState({
    name: '',
    region: '',
    city: '',
    district: '',
    coordinates: ['', '']
  });

  useEffect(() => {
    const fetchRegions = async () => {
      try {
        const res = await fetch('http://localhost:3001/region');
        const data = await res.json();
        setRegions(data);
      } catch (error) {
        console.error('Error fetching regions:', error);
      }
    };

    fetchRegions();
  }, []);

  const fetchCities = async () => {
    try {
      const res = await fetch('http://localhost:3001/city/region/' + region);
      const data = await res.json();
  
      // Format the data and extract city names
      const formattedData = data.map((city) => ({
        _id: city._id,
        name: city.name.name
      }));
  
      return formattedData;
    } catch (error) {
      console.error('Error fetching cities:', error);
      return [];
    }
  };
  

  const fetchDistricts = async (city) => {
    try {
      const res = await fetch(`http://localhost:3001/district/city/${city}`);
      const data = await res.json();
      setDistricts(data);
    } catch (error) {
      console.error('Error fetching districts:', error);
    }
  };

  const handleRegionChange = (e) => {
    const selectedRegion = e.target.value;
    setAttraction({ ...attraction, region: selectedRegion });
    fetchCities(selectedRegion);
  };

  const handleCityChange = (e) => {
    const selectedCity = e.target.value;
    setAttraction({ ...attraction, city: selectedCity });
    fetchDistricts(selectedCity);
  };

  const handleDistrictChange = (e) => {
    const selectedDistrict = e.target.value;
    setAttraction({ ...attraction, district: selectedDistrict });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAttraction({ ...attraction, [name]: value });
  };

  const handleCoordinateChange = (e, index) => {
    const updatedCoordinates = [...attraction.coordinates];
    updatedCoordinates[index] = e.target.value;
    setAttraction({ ...attraction, coordinates: updatedCoordinates });
  };

  const handleCreateAttraction = async () => {
    try {
      // Create region if a new one is selected
      let regionId = null;
      if (!regions.find((reg) => reg.name === attraction.region)) {
        const regionResponse = await fetch('http://localhost:3001/region', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            name: attraction.region
          })
        });
        const regionData = await regionResponse.json();
        regionId = regionData.id;
      } else {
        regionId = regions.find((reg) => reg.name === attraction.region).id;
      }

      // Create city if a new one is selected
      let cityId = null;
      if (!cities.find((cty) => cty.name === attraction.city)) {
        const cityResponse = await fetch('http://localhost:3001/city', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            name: attraction.city,
            regionId: regionId
          })
        });
        const cityData = await cityResponse.json();
        cityId = cityData.id;
      } else {
        cityId = cities.find((cty) => cty.name === attraction.city).id;
      }

      // Create district if a new one is selected
      let districtId = null;
      if (!districts.find((dist) => dist.name === attraction.district)) {
        const districtResponse = await fetch('http://localhost:3001/district', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            name: attraction.district,
            cityId: cityId
          })
        });
        const districtData = await districtResponse.json();
        districtId = districtData.id;
      } else {
        districtId = districts.find((dist) => dist.name === attraction.district).id;
      }

      // Create attraction
      const attractionResponse = await fetch('http://localhost:3001/attractions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: attraction.name,
          districtId: districtId,
          coordinates: attraction.coordinates
        })
      });

      if (attractionResponse.ok) {
        // Attraction creation successful
        // Reset form
        setAttraction({
          name: '',
          region: '',
          city: '',
          district: '',
          coordinates: ['', '']
        });

        // Display success message or redirect to attractions page
      } else {
        console.error('Error creating attraction');
      }
    } catch (error) {
      console.error('Error creating attraction:', error);
    }
  };

  return (
    <div>
      <h3>Create Attraction:</h3>
      <div>
        <label>Region:</label>
        <select value={attraction.region} onChange={handleRegionChange}>
          <option value="">Select Region</option>
          {regions.map((region) => (
            <option key={region.id} value={region.name}>
              {region.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>City:</label>
        <select value={attraction.city} onChange={handleCityChange} disabled={!attraction.region}>
          <option value="">Select City</option>
          {/* Render cities based on the selected region */}
          {cities
            .filter((city) => city.region === attraction.region)
            .map((city) => (
              <option key={city.id} value={city.name}>
                {city.name}
              </option>
            ))}
        </select>
      </div>
      <div>
        <label>District:</label>
        <select value={attraction.district} onChange={handleDistrictChange} disabled={!attraction.city}>
          <option value="">Select District</option>
          {/* Render districts based on the selected city */}
          {districts
            .filter((district) => district.city === attraction.city)
            .map((district) => (
              <option key={district.id} value={district.name}>
                {district.name}
              </option>
            ))}
        </select>
      </div>
      <div>
        <label>Attraction Name:</label>
        <input type="text" name="name" value={attraction.name} onChange={handleInputChange} />
      </div>
      <div>
        <label>Latitude:</label>
        <input
          type="text"
          value={attraction.coordinates[0]}
          onChange={(e) => handleCoordinateChange(e, 0)}
        />
      </div>
      <div>
        <label>Longitude:</label>
        <input
          type="text"
          value={attraction.coordinates[1]}
          onChange={(e) => handleCoordinateChange(e, 1)}
        />
      </div>
      <button onClick={handleCreateAttraction}>Create Attraction</button>
    </div>
  );
}

export default CreateAttraction;
