import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';

function AttractionGraph() {
  const [attractionData, setAttractionData] = useState([]);

  useEffect(() => {
    // Fetch attraction data
    const fetchAttractionData = async () => {
      try {
        const res = await fetch('http://localhost:3001/attraction');
        const data = await res.json();

        // Count attractions by region
        const regionCount = data.reduce((count, attraction) => {
          const region = attraction.district.city.region.name;
          count[region] = (count[region] || 0) + 1;
          return count;
        }, {});

        // Prepare data for chart
        const labels = Object.keys(regionCount);
        const counts = Object.values(regionCount);

        const chartData = {
          labels,
          datasets: [
            {
              label: 'Attractions by Region',
              data: counts,
              backgroundColor: 'rgba(75, 192, 192, 0.6)',
              borderColor: 'rgba(75, 192, 192, 1)',
              borderWidth: 1,
            },
          ],
        };

        setAttractionData(chartData);
      } catch (error) {
        console.error('Error fetching attraction data:', error);
      }
    };

    fetchAttractionData();
  }, []);

  return (
    <div>
      <h3>Attractions by Region:</h3>
      <div>
        {attractionData && attractionData.labels && attractionData.labels.length > 0 ? (
          <Bar data={attractionData} />
        ) : (
          <p>No attraction data available.</p>
        )}
      </div>
    </div>
  );
}

export default AttractionGraph;
