import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Photo from './Photo';

function Photos(){
    const [photos, setPhotos] = useState([]);
    const navigate = useNavigate();
    useEffect(function(){
        const getPhotos = async function(){
            const res = await fetch("http://localhost:3001/photos");
            const data = await res.json();
            setPhotos(data);
        }
        getPhotos();
    }, []);
    const handleIntersection = (photoId) => {
        console.log("view + 1")
        //incrementViewCount(photoId);
      };
    

    const handleClick = (photoId) => {
        navigate(`/photo/${photoId}`); // Use navigate to redirect to the photo details page
      };
    return(
        <div>
            <h3>Photos:</h3>
            <ul>
                {photos.map((photo) => (
                <Photo
                    photo={photo}
                    key={photo._id}
                    handleClick={() => handleClick(photo._id)} // Pass the click handler to the Photo component
                />
                ))}
             </ul>
        </div>
    );
}

export default Photos;
