import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


function PhotoDetails() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    const fetchPhotoDetails = async () => {
      try {
        const res = await fetch(`http://localhost:3001/photos/${id}`);
        const data = await res.json();
        setPhoto(data);
      } catch (error) {
        console.error('Error fetching photo details:', error);
      }
    };

    fetchPhotoDetails();
  }, [id]);

  if (!photo) {
    return <div>Slika ne obstaja</div>;
  }

  return (
    <div>

      <h2>Photo Details</h2>
      <p>Name: {photo.name}</p>
      <img src={"http://localhost:3001/" + photo.path} height="500px" alt={photo.name} />
      <p>Posted By: {photo.postedBy.username}</p>
      <p>Views: {photo.views}</p>
      <p>Likes: {photo.likes}</p>
      <form action="http://localhost:3001/comments/{photo._id}" method="POST">
        <input type="hidden" name="post" value="{photo}"/>
        <input type="hidden" name="post" value="{{post_id}}"/>
        <textarea name="text"></textarea>
        <button type="submit">Create Comment</button>
    </form>
    </div>

  );
}

export default PhotoDetails;
