import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import CommentForm from './CommentForm';
import Comments from './Comments';


function PhotoDetails() {
  const { id } = useParams();
  const [photo, setPhoto] = useState(null);
  const [comments, setComments] = useState(null);
  const navigate = useNavigate();

    useEffect(() => {
        const fetchPhotoDetails = async () => {
        try {
            const res = await fetch(`http://localhost:3001/photos/${id}`);
            const data = await res.json();
            setPhoto(data);
        } catch (error) {
            console.error('Error fetching photo details:', error);
        }
    };

    fetchPhotoDetails();
    }, [id]);
    useEffect(() => {
        const getComments = async () => {
        try {
            const res =  await fetch(`http://localhost:3001/comments/${photo._id}`, {method: 'GET'});
            const data = await res.json();
            setComments(data);
        } catch (error) {
            console.error('Error fetching photos:', error);
        }

        };

        getComments();
    }, []);

  if (!photo) {
    return <div>Slika ne obstaja</div>;
  }

  return (
    <div>

      <h2>Photo Details</h2>
      <p>Name: {photo.name}</p>
      <img src={"http://localhost:3001/" + photo.path} height="500px" alt={photo.name} />
      <p>Posted By: {photo.postedBy.username}</p>
      <p>Views: {photo.views}</p>
      <p>Likes: {photo.likes}</p>
      <CommentForm
            photo={photo}
            key={photo._id}
       />
       <Comments
            photo={photo}
            key={photo._id}
       />
    </div>

  );
}

export default PhotoDetails;
