import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // import Bootstrap CSS
import { Container, Row, Col } from 'react-bootstrap'; // import necessary Bootstrap components

function Landing() {
  const styles = {
    container: {
      height: '100vh', // makes the container full height
      display: 'flex', // necessary to do the centering
      justifyContent: 'center', // centers horizontally
      alignItems: 'center', // centers vertically
      backgroundImage: 'bg.png', // replace with your image URL
      backgroundSize: 'cover', // ensures the background image covers the whole container
      backgroundPosition: 'center', // centers the background image
    },
    text: {
      fontSize: '5rem', // makes the text very large
      color: 'white', // change according to your preference
      textShadow: '2px 2px 4px #000000', // gives the text a shadow to make it stand out against the background
    },
  };

  return (
    <Container style={styles.container}>
      <Row>
        <Col className="text-center">
          <h1 style={styles.text}>Welcome to OUTSIDERS</h1>
        </Col>
      </Row>
    </Container>
  );
}

export default Landing;
