import React, { useEffect } from 'react';
import { Bar } from 'react-chartjs-2';

function App() {
  useEffect(() => {
    fetchAttractionData();
  }, []);

  async function fetchAttractionData() {
    try {
      const [regions, cities, districts, attractions] = await Promise.all([
        fetch('http://localhost:3001/region').then((res) => res.json()),
        fetch('http://localhost:3001/city').then((res) => res.json()),
        fetch('http://localhost:3001/district').then((res) => res.json()),
        fetch('http://localhost:3001/attraction').then((res) => res.json())
      ]);

      console.log(regions)
      console.log(cities)
      console.log(districts)
      console.log(attractions)
    } catch (error) {
      console.error('Error fetching attraction data:', error);
    }
  }

  function createMap(regions, cities, districts, attractions) {
    // Implement map creation and plotting of attractions using mapping library of your choice
    // Use the attraction coordinates and other data to display markers on the map
  }

  function createGraph(attractions) {
    const regionCount = attractions.reduce((count, attraction) => {
      const region = attraction.district.city.region.name;
      count[region] = (count[region] || 0) + 1;
      return count;
    }, {});

    const labels = Object.keys(regionCount);
    const counts = Object.values(regionCount);

    const chartData = {
      labels,
      datasets: [
        {
          label: 'Attractions by Region',
          data: counts,
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    renderGraph(chartData);
  }

  function renderGraph(chartData) {
    return (
      <div>
        <h2>Attractions by Region</h2>
        <Bar data={chartData} />
      </div>
    );
  }

  return (
    <div>
      <h1>My App</h1>
      {renderGraph}
    </div>
  );
}

export default App;
